$count = 0;
$iconCount = count($icons);

// Process each tool
foreach ($icons as $slug => $svg) {
    // Find the tool entry by slug
    $pattern = "/(\'slug\' => \'{$slug}\',)/";
    if (preg_match($pattern, $content, $matches, PREG_OFFSET_CAPTURE)) {
        // Check if icon_name already exists
        $checkPos = $matches[0][1];
        $checkChunk = substr($content, $checkPos, 500);

        if (strpos($checkChunk, "'icon_name'") === false) {
            // Add icon_name and icon_svg after slug
            $escapedSvg = addslashes($svg);
            $replacement = "'slug' => '{$slug}',\n                'icon_name' => '{$slug}',\n                'icon_svg' => '{$escapedSvg}',";
            $content = preg_replace($pattern, $replacement, $content, 1);
            $count++;
            echo "âœ… Added icon to: {$slug}\n";
        } else {
            echo "â­ï¸  Skipped (already has icon): {$slug}\n";
        }
    }
}

// Save the modified content
file_put_contents($toolDataPath, $content);

echo "\n" . str_repeat("=", 60) . "\n";
echo "âœ… Successfully processed {$count} tools\n";
echo "ğŸ“Š Total icons in library: {$iconCount}\n";
