// Process each tool
foreach ($icons as $slug => $svg) {
    // Find the tool entry by slug
    $pattern = "/(\'slug\' => \'{$slug}\',)/";
    if (preg_match($pattern, $content)) {
        // Add icon_name and icon_svg after slug
        $replacement = "'slug' => '{$slug}',\n                'icon_name' => '{$slug}',\n                'icon_svg' => '{$svg}',";
        $content = preg_replace($pattern, $replacement, $content, 1);
        echo "Added icon to: {$slug}\n";
    }
}

// Save the modified content
file_put_contents($toolDataPath, $content);

echo "\nâœ… Successfully added icons to ToolData.php\n";
